import * as React from 'react';
import * as ReactDOM from 'react-dom';
import { DefaultCloseIcon, DefaultModalContent } from './components';
import {
  buildContentStyle,
  classnames,
  createElement,
  defaultProps,
  defferCall,
} from './helpers';
import { IModalProps, IModalState } from './types';
import './style.scss';

const ESC_KEY = 27;

class Modal extends React.Component<IModalProps, IModalState> {
  constructor(props: IModalProps) {
    super(props);
    this.state = {
      isInnerOpen: false,
    };
  }

  static defaultProps = defaultProps;

  static getDerivedStateFromProps(props: IModalProps, state: IModalState) {
    if (props.isOpen !== state.isInnerOpen && !props.unmountOnClose) {
      return {
        isInnerOpen: props.isOpen,
      };
    }
    return null;
  }

  componentDidMount() {
    document.addEventListener('keydown', this.handleKeyDown);
  }

  componentWillUnmount() {
    document.removeEventListener('keydown', this.handleKeyDown);
  }

  handleKeyDown = (event: KeyboardEvent) => {
    if (
      this.props.closeOnEscClick &&
      event.keyCode === ESC_KEY &&
      this.state.isInnerOpen
    ) {
      event.stopPropagation();
      this.handleClose();
    }
  };

  closeModal = () => this.setState({ isInnerOpen: false });

  handleAfterClose = () => {
    const { afterClose } = this.props;
    if (afterClose) {
      afterClose();
    }
  }

  handleClose = () => {
    const { beforeClose, requestClose, closeDebounceTimeout } = this.props;
    if (beforeClose) {
      beforeClose();
    }
    if (closeDebounceTimeout) {
      defferCall(
        () => {
          this.closeModal();
          requestClose();
          this.handleAfterClose();
        },
        closeDebounceTimeout,
      );
      return;
    }
    requestClose();
    this.handleAfterClose();
  }

  renderDimmer = () => {
    const { classes, closeOnDimmerClick } = this.props;
    return (
      <div
        className={
          classnames('hyper-dimmer-wrapper', classes && classes.contentClassName)
        }
        onClick={closeOnDimmerClick ? this.handleClose : undefined}
      />
    );
  }

  renderCloseIcon = () => {
    const { renderCloseIcon, closeOnCloseIconClick, closeIconPosition } = this.props;
    const iconClassNames = closeIconPosition
      ? classnames(
          'hyper-close-icon-wrapper',
          closeIconPosition.horizontal,
          closeIconPosition.vertical,
        )
      : 'hyper-close-icon-wrapper';
    return (
      <div
        className={iconClassNames}
        onClick={closeOnCloseIconClick ? this.handleClose : undefined}
        tabIndex={0}
      >
        {renderCloseIcon ? renderCloseIcon() : (<DefaultCloseIcon />)}
      </div>
    );
  }

  renderModalContent = () => {
    const {
      ariaEnabled,
      ariaProps,
      classes,
      isFullscreen,
      modalContentRef,
      renderContent,
      childrenMode,
      children,
    } = this.props;

    const content = childrenMode
      ? children
      : renderContent ? renderContent() : (null);

    return (
      <div
        className={
          classnames({
            'hyper-modal-content-wrapper': true,
            fullscreen: isFullscreen || false,
            [(classes && classes.contentClassName) || '']: true,
          })
        }
        ref={modalContentRef}
      >
        {content
          ? content
          : (<DefaultModalContent
              ariaProps={ariaProps}
              ariaEnabled={ariaEnabled}
              handleClose={this.handleClose}
            />)
        }
        {this.renderCloseIcon()}
      </div>
    );
  }

  renderModalWrapper = () => {
    const {
      classes,
      dimmerEnabled,
      modalWrapperRef,
      position,
    } = this.props;
    const { isInnerOpen } = this.state;
    return (
      <div
        className={
          classnames({
            'hyper-modal-wrapper': true,
            visible: isInnerOpen,
            [(classes && classes.wrapperClassName) || '']: true,
          })
        }
        ref={modalWrapperRef}
        style={buildContentStyle(position)}
      >
        {dimmerEnabled && this.renderDimmer()}
        {this.renderModalContent()}
      </div>
    );
  }

  renderModal = () => {
    const { portalMode, portalNode, classes, unmountOnClose } = this.props;
    const { isInnerOpen } = this.state;

    if (!isInnerOpen && unmountOnClose) {
      return (null);
    }
    if (portalMode && ReactDOM.createPortal) {
      const node = portalNode || createElement(classes && classes.portalWrapperClassName);
      return ReactDOM.createPortal(
        this.renderModalWrapper(),
        node
      );
    }
    return this.renderModalWrapper();
  }

  render() {
    return this.renderModal();
  }
}

export default Modal;

import * as React from 'react';
import './style.scss';

export const DefaultCloseIcon = () => {
  return (
    <i className="hyper-close-icon" />
  );
}

import * as React from 'react';
import { IARIAProps } from '../../types';

import './style.scss';

export interface IDefaultModalContentProps {
  ariaEnabled?: boolean;
  ariaProps?: IARIAProps;
  handleClose: () => void;
}

export const DefaultModalContent = (props: IDefaultModalContentProps) => {
  const { ariaEnabled, ariaProps, handleClose } = props;
  const labelId = ariaEnabled && ariaProps
    ? ariaProps['aria-labelledby']
    : undefined;
  const descriptionId = ariaEnabled && ariaProps
    ? ariaProps['aria-describedby']
    : undefined;

  return (
    <div className="hyper-modal-default-content">
      <div className="title" id={labelId}>Hyper modal</div>
      <div className="description" id={descriptionId}>
        Fully customizable and accessible modal
      </div>
      <button tabIndex={1} onClick={handleClose}>Close modal</button>
    </div>
  );
};

import { IPositionProps } from '../types';
import { defaultProps } from './defaultProps';

export const buildContentStyle = (position?: IPositionProps) => {
  const defaultStyles = {
    display: 'flex',
    ...defaultProps.position,
  };
  if (position) {
    const style = Object.assign(
      {},
      defaultStyles,
      { ...position },
    );
    return style;
  }
  return defaultStyles;
}

export type IClassNamesObjectProps = { [key: string]: boolean };

export type IClassNamesProps = string | undefined;

export const convertObjectToString = (classes: { [key: string]: boolean }): string => {
  return Object
    .keys(classes)
    .filter(key => !!classes[key])
    .reduce(
      (classString, item) => (classString
        ? `${classString}${item ? ` ${item}` : ''}`
        : `${item}`),
      '',
    );
}

export const classnames = (
  ...classes: (IClassNamesObjectProps | IClassNamesProps)[]
) => {
  if (classes[0] && typeof classes[0] === 'string') {
    return classes.join(' ');
  }
  return convertObjectToString(classes[0] as IClassNamesObjectProps);
}

export const createElement = (className?: string): HTMLElement => {
  let currentNode = document.getElementById('hyper-modal-portal-id');
  if (!currentNode) {
    currentNode = document.createElement('div');
    currentNode.setAttribute('id', 'hyper-modal-portal-id');
    document.body.appendChild(currentNode);
  }
  if (className && !currentNode.classList.contains(className)) {
    currentNode.classList.add(className);
  }
  return currentNode;
}

export const defaultProps = {
  afterClose: undefined,
  ariaEnabled: true,
  ariaProps: {
    'aria-describedby': 'hyper-modal-description',
    'aria-labelledby': 'hyper-modal-title',
    role: 'dialog',
  },
  beforeClose: undefined,
  classes: undefined,
  childrenMode: true,
  closeDebounceTimeout: 0,
  closeIconPosition: {
    vertical: 'top',
    horizontal: 'right',
  },
  closeOnCloseIconClick: true,
  closeOnDimmerClick: true,
  closeOnEscClick: true,
  dimmerEnabled: true,
  isFullscreen: false,
  portalMode: false,
  portalNode: undefined,
  position: {
    alignItems: 'center',
    justifyContent: 'center',
  },
  renderCloseIcon: undefined,
  renderContent: undefined,
};

export const defferCall = (func: any, timeout: number = 500, args: any = {}) => {
  return new Promise(resolve => {
    setTimeout(
      () => {
        func(args);
        resolve();
      },
      timeout,
    );
  });
}

export * from './buildStyles';
export * from './classnames';
export * from './createElement';
export * from './defaultProps';
export * from './defferCall';
